import { useEffect } from "react"

import { fetchDashboardData } from "../utils/api-client"

import Dashboard from "./Dashboard"
import CircularProgress from "@material-ui/core/CircularProgress"
import { connect } from "react-redux"
import { setPageLimit } from "../store/actions/pagination"

export const pageLimitConstant = 50
function App({ dispatch, loading, recordsParsed, ticketCount }) {
  const pageNumber = ticketCount / pageLimitConstant

  useEffect(() => {
    dispatch(loadingStart())
    if (recordsParsed == null) {
      dispatch(setPageLimit(pageLimitConstant))
      dispatch(fetchDashboardData(0))
    }
    if (recordsParsed == ticketCount - 20)
      dispatch(fetchDashboardData(pageNumber))
  }, [recordsParsed])

  return <div>{loading ? <CircularProgress /> : <Dashboard />}</div>
}
function mapStateToProps({ authToken, pagination, tickets, loading = false }) {
  return {
    loading,
    recordsParsed: pagination.recordsParsed,
    pageLimit: pagination.pageLimit,
    ticketCount: Object.keys(tickets).length,
  }
}

export default connect(mapStateToProps)(App)
