import { ObjectLiteral } from "../../models/types"

export const SET_PAGE = "SET_PAGE"
export const SET_PAGELIMIT = "SET_PAGELIMIT"
export const SET_TOTAL_COUNT = "SET_TOTAL_COUNT"
export function setTotalCount(totalCount: number): ObjectLiteral {
  return {
    type: SET_TOTAL_COUNT,
    totalCount,
  }
}

export function setPage(page: number): ObjectLiteral {
  return {
    type: SET_PAGE,
    page,
  }
}
export function setPageLimit(pageLimit: number): ObjectLiteral {
  return {
    type: SET_PAGELIMIT,
    pageLimit,
  }
}
