import { TICKET_DETAILS_REQUESTED } from "../actions/ticketDetails"

export default function detailsRequested(state = {}, action) {
  switch (action.type) {
    case TICKET_DETAILS_REQUESTED:
      return { ...state, ticketDetails: action.id}

    default:
      return state
  }
}
