import React from "react"
import Drawer from "@material-ui/core/Drawer"
import TicketDetail from "./TicketDetail"
import { SIDEBAR_CLOSE } from "../store/actions/sidebard"
import { connect } from "react-redux"

const Sidebar = ({ dispatch, isOpen, ticket }) => {
  return (
    <div>
      <Drawer
        anchor="right"
        open={isOpen}
        onClose={() => dispatch({ type: SIDEBAR_CLOSE })}
      >
        {<TicketDetail ticket={ticket} />}
      </Drawer>
    </div>
  )
}

const mapStateToProps = (state) => {
  const { sidebar, ticket, tickets } = state
  //sidebar.sidebarRequested:
  return {
    isOpen: sidebar ? sidebar.sidebarRequested : false,
    ticket: tickets.tickets ? tickets.tickets[sidebar.sidebarRequested] : null,
  }
}
export default connect(mapStateToProps)(Sidebar)
