import { SIDEBAR_CLOSE, SIDEBAR_OPEN } from "../actions/sidebard"

export default function sidebar(state = null, action) {
  switch (action.type) {
      case SIDEBAR_OPEN:
          return { true}
    case SIDEBAR_CLOSE:
      return false
    default:
      return state
  }
}
