import { SET_PAGE } from "../actions/pagination"

export default function pagination(state = {}, action) {
  console.log(state)
  switch (action.type) {
    case SET_PAGE:
      return {
        ...state,
        pagination: { currentPage: action.page },
      }
    default:
      return state
  }
}
