import Drawer from "@material-ui/core/Drawer"
import Pagination from "./Pagination"
import TicketDetail from "./TicketDetail"
import { connect } from "react-redux"
import Tickets from "./Tickets"

function Dashboard({ tickets, ticketDetails }) {
  return (
    <div>
      <Tickets tickets={tickets} />
      <Pagination />
      <Drawer anchor="right" open={!!ticketDetails}>
        {<TicketDetail />}
      </Drawer>
    </div>
  )
}

function mapStateToProps({ tickets }) {
  let mappedTickets = []
  let data = tickets
  if (data.tickets) return {}
  let ticketIds = Object.keys(data.tickets)
  if (data.tickets && ticketIds.length > 1) {
    mappedTickets = ticketIds.map((id) => data.tickets[id])
  }

  return { tickets: mappedTickets }
}
export default connect(mapStateToProps)(Dashboard)
