import { RECEIVE_TICKETS } from "../actions/tickets"

function normalizeTickets(tickets) {
  return tickets.reduce(function (ticket, item) {
    ticket[ticket.allFields.id] = item
    return ticket
  }, {})
}
export default function tickets(state = {}, action) {
  switch (action.type) {
    case RECEIVE_TICKETS:
      return {
        ...state,
        tickets: normalizeTickets(normalizeTickets(action.tickets)),
      }
    default:
      return state
  }
}
