import { ObjectLiteral, Ticket } from "../../models/types"
import { RECEIVE_TICKETS } from "../actions/tickets"

function convertToClientModel({ allFields }): Ticket {
  return {
    assignee: allFields.assignee,
    shortDescription: allFields.shortDescription,
    description: allFields.description,
    application: allFields.application,
    made_sla: allFields.made_sla,
    upon_reject: allFields.upon_reject,
    opened_by: allFields.opened_by,
    priority: allFields.priority,
    activity_due: allFields.activity_due,
    approval: allFields.approval,
    number: allFields.number,
    state: allFields.state,
  } as Ticket
}

function normalizeTickets(tickets: [Record<string, unknown>]) {
  return tickets.reduce(function (ticket, item) {
    ticket[item.allFields.id as string] = convertToClientModel(item)
    return ticket
  }, {})
}
