import { Ticket } from "../../models/types"
import { RECEIVE_TICKETS } from "../actions/tickets"

function convertToClientModel({ allFields }): Ticket {
  return {
    assignee: full.assignee,
    shortDescription: full.shortDescription,
    description: full.description,
    application: full.application,
    made_sla: full.made_sla,
    upon_reject: full.upon_reject,
    opened_by: full.opened_by,
    priority: full.priority,
    activity_due: full.activity_due,
    approval: full.approval,
    number: full.number,
    state: full.state,
  } as Ticket
}

function normalizeTickets(tickets) {
  return tickets.reduce(function (ticket, item) {
    ticket[item.allFields.id] = item.allFields
    return ticket
  }, {})
}
export default function tickets(state = {} as any, action) {
  switch (action.type) {
    case RECEIVE_TICKETS:
      return {
        ...state,
        ...normalizeTickets(action.tickets),
      }

    default:
      return state
  }
}
