import { getToken } from "./auth-provider"

async function client(endpoint: string, customConfig = {}): Promise<{}> {
  const token = await getToken()
  const config = {
    method: "GET",
    headers: {
      apiToken: `eyJhbGciOiJSUzUxMiIsInppcCI6IkRFRiJ9.eNpsj80KwjAQhN9lz0EoaW3pyaJFRAsiuYmUNF0kaJOQHxRK3930IAj2tszMtzs7ggsdlCC46mXPPbYenU-AAA-9RCUwmi_soiAsRr-HMlknaUHzPKMppQQM2kE6J7VyUF5HUHyYIda07LA91qxldXM-VaxuL3W1i4uM1ZHxEmN-nCaygCwnbwTuVgcz34mzl-KBfv-j4Nt86xU0i91w4PL5_96GC4HKB4sroQeYPgAAAP__.AovMqXebA9AVWlvGgSc4FEEQ6YiT-4RIo2a1Y74PwTdQYUY7q25Y4o3HPb2H7AoX_FzUAzRyx1xvmuzYaAlDSaXZs9gQ9voNIiwnvjIqcNyVuNs4-fbkgob7QnRrorDF87C2KxDxbPys7WECQPBhMYBPdc9LLYzGkBftQG45SEg`,
      "Access-Control-Allow-Origin": "*",
    },
    ...customConfig,
  }
  //`https://dh-atrpackageinstalltest.atrmywizard-aiops.com/${endpoint}`
  //${process.env.API_URL}/${endpoint}
  const response = await window.fetch(
    `https://dh-atrpackageinstalltest.atrmywizard-aiops.com/atr-gateway/ticket-management/api/v1/tickets?ticketType=incident&sortDirection=DESC&page=0&perPage=10`,
    config
  )
  const data = await response.json()
  if (response.ok) {
    console.log(data)
    return data
  } else {
    return Promise.reject(data)
  }
}

async function getTickets(page: number = 0) {
  const TICKET_URL = `atr-gateway/ticket-management/api/v1/tickets?ticketType=incident&sortDirection=DESC&page=${page}&perPage=10`

  //console.log(token)

  return client(TICKET_URL).then(
    (responseData) => responseData,
    (errorData) => errorData
  )
}

export { client, getTickets }
