import React from "react"
import { makeStyles } from "@material-ui/core/styles"
import Typography from "@material-ui/core/Typography"
import Button from "@material-ui/core/Button"
import { connect } from "react-redux"

const useStyles = makeStyles({
  root: { fontSize: "12px", display: "flex", justifyContent: "center" },
  pagination: {
    listStyleType: "none",
    margin: 0,
    display: "flex",
    padding: "1rem",
  },
  status: {
    display: "flex",
    alignItems: "center",
  },
  statusText: {
    fontSize: "12px",
  },
  button: {
    fontSize: "12px",
  },
})

const Pagination = ({ totalRecords, currentPage, pageLimit, onSetPage }) => {
  const classes = useStyles()
  const totalPages = Math.ceil(totalRecords / pageLimit)
  return (
    <div className={classes.root}>
      <ul className={classes.pagination}>
        <li className="page-item">
          <Button
            className={classes.button}
            onClick={() => onSetPage(currentPage - 1)}
          >
            BACK
          </Button>
        </li>

        <li className={classes.status}>
          <span></span>
          <Typography className={classes.statusText} color="textSecondary">
            Page {currentPage} of {totalPages}
          </Typography>
        </li>
        <li className="page-item">
          <Button
            className={classes.button}
            onClick={() => onSetPage(currentPage + 1)}
          >
            NEXT
          </Button>
        </li>
      </ul>
    </div>
  )
}
const mapStateToProps = ({ pagination }) => {
  return {
    totalRecords: pagination.totalRecords,
    currentPage: pagination.currnetPage,
    pageLimit: pagination.pageLimit,
  }
}

const mapDispatchToProps = (dispatch) => ({
  onSetPage: (page, payload) => dispatch({ type: "SET_PAGE", page, payload }),
})
export default connect(mapStateToProps, mapDispatchToProps)(Pagination)
