import { client } from "./api-client"
import { safeStorageGet, safeStorageSet } from "./storage-client"
const TOKEN_URL = `/atr-gateway/identity-management/api/v1/auth/short-token?useDeflate=true`
const TOKEN_KEY = "apiToken"
async function getToken(): Promise<string> {
  let token = safeStorageGet(TOKEN_KEY)
  if (token) {
    return token
  } else {
    const user = {
      username: "candidate_test1",
      password: "candidate_test1",
      snowEnabled: true,
    }
    const headers: HeadersInit = {
      method: "POST",
      accept: "*/*",
      "Content-Type": "application/json",
    }

    return client(TOKEN_URL, {
      method: "POST",
      body: JSON.stringify(user),
      headers,
    }).then((response) => {
      const value = (response as any).token

      safeStorageSet(TOKEN_KEY, value)
      return (response as any).token as string
    })
  }
  //return `eyJhbGciOiJSUzUxMiIsInppcCI6IkRFRiJ9.eNpsj0sLwjAQhP_LnoMYfNWeLFpEtCCSm0hJk0WC5kGSolD6300PgmBvy8x8u7MdhLaBHAQ3UkkesY4YIgUCvJUKjcBkvrBJgvCYfAk5XdJ5NpsuaLZaZwQceq1CUNYEyK8dGK4HiFU1O2yPJatZWZ1PBSvrS1ns0iLnbWKiwpTv-p6MIOPJG4G7t60b7qQ5KvHAuP9R8O2-9SilqRtqrp7_7224EGhi63EirIb-AwAA__8.IbxykUQEPStnIXwSigDLAL7ZImS50O_wBOUsDE60KY6OqwNsZFCsGvAVNxoe9k-_F_2M2X_P__MNIziIwiSmvPRZ_bn8_LcTSs1oqE1HyiXUDFUsNuORoy7kBDhj2m05ax0cmAxtTRl6Vfum-6MzH9Q1vZokJxNjcIE8LTJ3IXc`
}

export { getToken }
